     resource 'vers' (1) {
          0x00,               // Major release: digit before decimal
          0x05,               // Minor release: digits after decimal
          release,               // dev/alpha/beta/release
          0x02,               // Build # (for pre-release)
          0,                  // Region code (US)
          "0.5.2 (OSX)",        // Major/minor release as short string
          "0.5.2, © 2004 CNMAT (OSX Version)"
          };
