     resource 'vers' (1) {          0x01,               // Major release: digit before decimal          0x02,               // Minor release: digits after decimal          release,               // dev/alpha/beta/release          0x00,               // Build # (for pre-release)          0,                  // Region code (US)          "1.3 (OSX)",        // Major/minor release as short string          "1.3, © 2003 CNMAT (OSX Version)"          };