void rdistsig_assist(t_rdist *x, void *b, long m, long a, char *s){
	if (m == ASSIST_OUTLET)
		sprintf(s,"Probability distribution and arguments");
	else {
		switch (a) {	
		case 0:
			sprintf(s,"Random variate");
			break;
		}
	}
}

void *rdistsig_new(t_symbol *msg, short argc, t_atom *argv){
	t_rdist *x;

	x = (t_rdist *)newobject(rdistsig_class); // create a new instance of this object
	
	dsp_setup((t_pxobject *)x, 1);
	outlet_new((t_object *)x, "signal");
	
	x->r_bufferSize = rdist_primes[++rdistsig_numInstances]; // num of instances gets put temporarily in x->r_bufferSize.

	x->r_buffers = (float **)calloc(2, sizeof(float *));
	x->r_buffers[0] = (float *)calloc(x->r_bufferSize, sizeof(float));
	x->r_buffers[1] = (float *)calloc(x->r_bufferSize, sizeof(float));

	x->r_useBuffer = 1;
	x->r_bufferPos = (int)(x->r_bufferSize * (rand() / ((double)RAND_MAX + 1)));	

	x->r_sampleAndHold = 0;
	x->r_defVal = 0.f;

	rdist_init(x, argc, argv);

	return(x);
}

void rdistsig_useBuffer(t_rdist *x, long b){
	if(x->r_useBuffer == b) return;
	int dsp_was_on = sys_getdspstate();

	dspmess(gensym("stop"));
	x->r_useBuffer = b;
	x->r_should_refill_buffer = 0;
	if(b){
		x->r_should_refill_buffer = 1;
		rdist_fillBuffers((void *)x);
		x->r_bufferPos = (int)(x->r_bufferSize * (rand() / ((double)RAND_MAX + 1)));	
		x->r_whichBuffer = abs(x->r_whichBuffer - 1);
		rdist_fillBuffers((void *)x);
	}
	if(dsp_was_on) dspmess(gensym("start"));
}

void rdistsig_setDefaultValue(t_rdist *x, double v){
        x->r_defVal = (float)v;
}

void rdistsig_sampleAndHold(t_rdist *x, int v){
        x->r_sampleAndHold = v;
}

void rdistsig_free(t_rdist *x){
	x->r_should_refill_buffer = 0;
	dsp_free((t_pxobject *)x);
	gsl_rng_free(x->r_rng);
	free(x->r_buffers);
	--rdistsig_numInstances;
}

void rdistsig_dsp(t_rdist *x, t_signal **sp, short *count){
	if(count[0]){
		if(x->r_useBuffer)
			dsp_add(rdistsig_perform_connected, 4, x, sp[0]->s_vec, sp[1]->s_vec, sp[0]->s_n);
		else dsp_add(rdistsig_perform_connected_nobuffer, 4, x, sp[0]->s_vec, sp[1]->s_vec, sp[0]->s_n);
	}else{
		if(x->r_useBuffer)
			dsp_add(rdistsig_perform, 3, x, sp[1]->s_vec, sp[0]->s_n);
		else dsp_add(rdistsig_perform_nobuffer, 3, x, sp[1]->s_vec, sp[0]->s_n);
	}
}

t_int *rdistsig_perform(t_int *w){
	t_rdist *x = (t_rdist *)w[1];
	t_float *out = (t_float *)w[2];
	int n = (int)w[3];
	int i;

	for(i = 0; i < n; i++){
		out[i] = x->r_buffers[x->r_whichBuffer][x->r_bufferPos];
		rdist_incBufPos(x);
		//(*x->r_function)(x->r_rng, x->r_vars, x->r_data);
		//out[i] = x->r_data[0];
	}

	return (w + 4);
}

t_int *rdistsig_perform_nobuffer(t_int *w){
	t_rdist *x = (t_rdist *)w[1];
	t_float *out = (t_float *)w[2];
	int n = (int)w[3];
	int i;

	for(i = 0; i < n; i++){
		//out[i] = x->r_buffers[x->r_whichBuffer][x->r_bufferPos];
		//rdist_incBufPos(x);
		(*x->r_function)(x);
		out[i] = x->r_data[0];
	}

	return (w + 4);
}

t_int *rdistsig_perform_connected(t_int *w){
	t_rdist *x = (t_rdist *)w[1];
	t_float *in = (t_float *)w[2];
	t_float *out = (t_float *)w[3];
	int n = (int)w[4];
	int i;

	for(i = 0; i < n; i++){
		out[i] = in[i] ? x->r_buffers[x->r_whichBuffer][x->r_bufferPos] : x->r_defVal;
		in[i] ? rdist_incBufPos(x) : NULL;
		x->r_defVal = x->r_sampleAndHold ? out[i] : x->r_defVal;
		//(*x->r_function)(x->r_rng, x->r_vars, x->r_data);
		//out[i] = x->r_data[0];
	}

	return (w + 5);
}

t_int *rdistsig_perform_connected_nobuffer(t_int *w){
	t_rdist *x = (t_rdist *)w[1];
	t_float *in = (t_float *)w[2];
	t_float *out = (t_float *)w[3];
	int n = (int)w[4];
	int i = 0;
	int j;

	while(i < n){
		if(in[i]){
			(*x->r_function)(x);
			for(j = 0; j < x->r_stride; j++){
				out[i] = x->r_data[j];
				++i;
			}
		}
		x->r_defVal = x->r_sampleAndHold ? out[i] : x->r_defVal;
	}

	return (w + 5);
}
